function createSensorObject(root, name, varname, unique_id, unit_of_measurement)
{
    let vjson = {
        "stat_t": "~/" + varname + "/state",
        "dev": {
            "manufacturer": "PA1DVB",
            "model": "Node-Red",
            "name": "BatteryControl",
            "ids": []
        }
    };

    let device_identifier = unique_id;
    if (device_identifier.indexOf("_") != -1)
    {
        device_identifier = unique_id.split("_")[0];
    }

    vjson.dev.ids.push(device_identifier);
    vjson["~"] = root;
    vjson["name"] = name;
    vjson["uniq_id"] = unique_id;
    vjson["unit_of_meas"] = unit_of_measurement;

    let state_class = "measurement";

    let device_class = "";
    switch (unit_of_measurement)
    {
        case "text":
            state_class = "";
            break;
        case "%":
            if (
                (name.indexOf('Batt') !== -1)
                || (name.indexOf('batt') !== -1)
                || (varname.indexOf('_soc') !== -1)
            ) {
                device_class = "battery";
            } else {
                device_class = "percentage";
            }
            break;
        case "°C":
        case "C":
        case "°F":
        case "F":
            device_class = "temperature";
            break;
        case "V":
        case "mV":
            device_class = "voltage";
            break;
        case "A":
        case "mA":
            device_class = "current";
            break;
        case "\u00b0c":
        case "\u00b0C":
            device_class = "temperature";
            break;
        case "W":
            device_class = "power";
            break;
        case "kWh":
        case "Wh":
            device_class = "energy";
            state_class = "total_increasing";
            break;
    }

    if (device_class != "")
    {
        vjson["device_class"] = device_class;
    }
    if (state_class != "")
    {
        vjson["state_class"] = state_class;
    }

    //vjson["val_tpl"] = "{{ value_json." + varname + " }}";

    return vjson
}

