function sanityCheck(json) {
    if (json.status != true) {
        node.error("Invalid Tibber result!", msg);
        return false;
    }
    var jDate = new Date(json.datum);
    var tDate = new Date();
    tDate.setHours(0, 0, 0, 0);

    if (jDate.getTime() < tDate.getTime()) {
        node.error("Incorrect date in Tibber result!");
        return false;
    }

    //We should have at least 24 data items (96 for quarter prices)
    let tot_results = Object.keys(json.data).length;
    if (tot_results < 24) {
        node.error("Incorrect data in Tibber result!");
        return false;
    }
    return true;
}

function calculate_charge_schema(data) {
    const processedData = JSON.parse(JSON.stringify(data)); // Deep copy

    const bIsQuarterData = (processedData.length > 24);
    const blockMultiply = (bIsQuarterData == true) ? 0.25 : 1;

    const calc_charge_rate = context.global.batt_charge_rate * blockMultiply;
    const calc_discharge_rate = context.global.batt_discharge_rate * blockMultiply;

    // Calculate average price for reference
    const prices = processedData.map(item => item.hour_price);
    const avgPrice = prices.reduce((a, b) => a + b, 0) / prices.length;

    // Find price extremes for charging (low prices) and discharging (high prices)
    const sortedByPrice = [...processedData].sort((a, b) => a.hour_price - b.hour_price);

    // Strategy: charge at lowest prices, discharge at highest prices
    // Ensure equal charge/discharge periods
    let chargeSlots = [];
    let dischargeSlots = [];

    // Find potential charge slots (lowest prices)
    for (let i = 0; i < sortedByPrice.length; i++) {
        const item = sortedByPrice[i];
        const potentialDischarge = sortedByPrice[sortedByPrice.length - 1 - i];

        // Check if price difference is at least the sell threshold
        if (potentialDischarge.hour_price - item.hour_price >= context.flow.price_min_sell_difference) {
            chargeSlots.push(item.datum);
            dischargeSlots.push(potentialDischarge.datum);
        }
    }

    // Calculate max slots to max_soc
    let maxSlots = Math.ceil(context.flow.batt_charged_capacity / calc_charge_rate);

    // Ensure equal number of charge and discharge slots
    let minSlots = Math.min(chargeSlots.length, dischargeSlots.length);

    // Ensure we do not charge more then needed
    minSlots = Math.min(minSlots, maxSlots);

    chargeSlots = chargeSlots.slice(0, minSlots);
    dischargeSlots = dischargeSlots.slice(0, minSlots);

    // Apply actions to data
    processedData.forEach(item => {
        if (chargeSlots.includes(item.datum)) {
            item.hour_type = 'charge';
        } else if (dischargeSlots.includes(item.datum)) {
            item.hour_type = 'discharge';
        } else {
            item.hour_type = 'idle';
        }
    });

    // Ensure first action is CHARGE by sorting if needed
    const firstChargeIndex = processedData.findIndex(item => item.hour_type === 'charge');
    const firstDischargeIndex = processedData.findIndex(item => item.hour_type === 'discharge');

    if (firstDischargeIndex < firstChargeIndex && firstDischargeIndex !== -1) {
        // Swap the first discharge with a later charge to ensure charge comes first
        const firstDischarge = processedData[firstDischargeIndex];
        const laterCharge = processedData.find((item, idx) =>
            idx > firstDischargeIndex && item.hour_type === 'charge'
        );

        if (laterCharge) {
            firstDischarge.hour_type = 'charge';
            laterCharge.hour_type = 'discharge';
        }
    }

    // Apply charged percentage to data (Assume we start empty)
    let battery_capacity_start = context.flow.batt_min_charged;

    let battery_capacity_watt = battery_capacity_start;
    let earned_money = 0.0;

    processedData.forEach(item => {
        if (item.hour_type == 'charge') {
            const power_2_add = Math.min(
                calc_charge_rate,
                context.global.batt_capacity - battery_capacity_watt
            );            
            if (power_2_add > 0) {
                battery_capacity_watt += power_2_add;
                earned_money -= (power_2_add / 1000) * item.hour_price;
            }
            item.setpoint = context.global.batt_charge_rate;
        } else if (item.hour_type == 'discharge') {
            let power_2_sub = Math.max(0, Math.min(calc_discharge_rate, battery_capacity_watt - context.flow.batt_min_charged));
            if (power_2_sub > 0) {
                battery_capacity_watt -= power_2_sub;
                earned_money += (power_2_sub / 1000) * item.hour_price;
            }
            item.setpoint = -context.global.batt_discharge_rate;
        } else {
            item.setpoint = context.global.idle_rate;
        }
        item.battery_capacity_watt = battery_capacity_watt;
        item.battery_capacity_percentage = parseFloat(((100.0 / context.global.batt_capacity) * item.battery_capacity_watt).toFixed(2));
        item.earned_money = parseFloat(earned_money.toFixed(4));
    });

    return processedData;
}

