let atime = Date.now();

for (var key in data_msg)
{
    let timestamp = 0;
    if (data_timestamp.hasOwnProperty(key)) {
        timestamp = data_timestamp[key];
    }
    if (data_msg.hasOwnProperty(key))
    {
        let timediff = (atime - timestamp) / 1000;
        if (timediff < 60)
            continue;
        //node.warn("key: " + key + ", value: " + data_msg[key] + ", time: "  + timestamp);
        msg.topic = key;
        msg.payload = data_msg[key];
        msg.sendnow = true;
        node.send(msg);
    }
}
